function initNoticiasSys()
{
	if(isObject($noticias_handler))
		return;
		
		
	$NoticiasSysNum++;

	$noticias_handler = newSimObj("noticias_handler", $NoticiasSysNum);
	$noticias_handler.initSimNoticias();
	$noticias_handler.sortearNovaNoticia();
}

function noticias_handler::initSimNoticias(%this)
{
	if(!isObject(%this.simNoticias))
	{
		%this.simNoticias = new SimSet();
		%this.criarTodasAsNoticias();
	}	
}

function noticias_handler::criarNoticia(%this, %titulo, %texto, %img, %just, %req, %reqQtdMin, %reqQtdMax, %especie, %prioridade)
{
	$noticiasObjNum++;
	%noticia = newSimObj("noticia_obj", $noticiasObjNum);	
	%noticia.num = $noticiasObjNum;
	%noticia.titulo = %titulo;
	%noticia.texto = %texto;
	%noticia.img = %img;
	%noticia.just = %just;
	%noticia.req = %req;
	%noticia.reqQtdMin = %reqQtdMin;
	%noticia.reqQtdMax = %reqQtdMax;
	%noticia.especie = %especie;
	%noticia.prioridade = %prioridade;
	
	%this.simNoticias.add(%noticia);
}

