function noticias_handler::setNoticia(%this, %num)
{
	%noticia = %this.getNoticiaPorNumero(%num);
	noticias_tab_titulo_txt.text = %noticia.titulo;
	noticias_tab_txtML.setText("<just:" @ %noticia.just @ ">" @ %noticia.texto @ "\n\n");
	noticias_tab_img.bitmap = "game/data/images/noticias/" @ %noticia.img;
}

function noticias_handler::getNoticiaPorNumero(%this, %num)
{
	for(%i = 0; %i < %this.simNoticias.getCount(); %i++)
	{
		%noticia = %this.simNoticias.getObject(%i);
		if(%noticia.num == %num)
			return %noticia;
	}
}

function noticias_handler::sortearNovaNoticia(%this)
{
	%result = getRandom(%this.simNoticias.getCount());
	%this.setNoticia(%result);
}




